const { test, expect } = require('@playwright/test');

test.describe('DAY 1: Foundations & Basic Navigation', () => {

  test.beforeEach(async ({ page }) => {
    // This runs before every test
    console.log('üîµ Starting test...');
  });

  test('TC1.1 - Navigate to website and verify title', async ({ page }) => {
    // Arrange: Navigate to website
    await page.goto('https://example.com');

    // Assert: Verify title
    await expect(page).toHaveTitle(/Example/);

    console.log('‚úÖ TC1.1 Passed');
  });

  test('TC1.2 - Verify page heading exists', async ({ page }) => {
    // Arrange
    await page.goto('https://example.com');

    // Assert
    const heading = page.locator('h1');
    await expect(heading).toBeVisible();

    console.log('‚úÖ TC1.2 Passed');
  });

  test('TC1.3 - Test different selector types', async ({ page }) => {
    // Arrange
    await page.goto('https://example.com');

    // Test CSS selector
    const cssSelector = page.locator('a');
    await expect(cssSelector).toBeAttached();
    console.log('‚úÖ CSS selector works');

    // Test text selector
    const textSelector = page.locator('text=More information');
    await expect(textSelector).toBeVisible();
    console.log('‚úÖ Text selector works');
  });

  test('TC1.4 - Test navigation and go back', async ({ page }) => {
    // Arrange: Start at homepage
    await page.goto('https://example.com');
    await expect(page).toHaveURL(/example.com/);

    // Act: Click link
    await page.locator('text=More information').click();

    // Assert: URL changed
    await expect(page).toHaveURL(/.*more-info/);
    console.log('‚úÖ Navigation successful');

    // Act: Go back
    await page.goBack();

    // Assert: Back to original
    await expect(page).toHaveURL(/example.com/);
    console.log('‚úÖ Go back successful');
  });

  test('TC1.5 - Test element visibility checks', async ({ page }) => {
    // Arrange
    await page.goto('https://example.com');
    const element = page.locator('h1');

    // Assert: Multiple checks
    await expect(element).toBeVisible();
    console.log('‚úÖ Element is visible');

    await expect(element).not.toBeHidden();
    console.log('‚úÖ Element is not hidden');

    await expect(element).toBeAttached();
    console.log('‚úÖ Element is attached to DOM');
  });

});

const { test, expect } = require('@playwright/test');

test.describe('DAY 1: Foundations & Basic Navigation', () => {

  test.beforeEach(async ({ page }) => {
    // This runs before every test
    console.log('üîµ Starting test...');
    await page.goto('https://demo.playwright.dev/todomvc/');
  });

  test('TC1.1 - Navigate to website and verify title', async ({ page }) => {
