    await expect(page).toHaveTitle(/TodoMVC/);

    console.log('‚úÖ TC1.1 Passed - Title verified');
  });

  test('TC1.2 - Verify page heading and input field exist', async ({ page }) => {
    // Arrange: Page loaded (done in beforeEach)

    // Assert: Verify heading exists
    const heading = page.locator('h1');
    await expect(heading).toBeVisible();
    console.log('‚úÖ Heading found');

    // Assert: Verify input field exists
    const input = page.locator('input[placeholder="What needs to be done?"]');
    await expect(input).toBeVisible();
    console.log('‚úÖ Input field found');
  });

  test('TC1.3 - Test different selector types on same element', async ({ page }) => {
    // Arrange
    const inputField = page.locator('input[placeholder="What needs to be done?"]');

    // Test CSS selector
    await expect(inputField).toBeAttached();
    console.log('‚úÖ CSS selector works');

    // Test by role selector (BEST PRACTICE)
    const inputByRole = page.locator('role=textbox');
    await expect(inputByRole).toBeVisible();
    console.log('‚úÖ Role selector works');

    // Test getting by placeholder
    const inputByPlaceholder = page.locator('[placeholder*="needs"]');
    await expect(inputByPlaceholder).toBeVisible();
    console.log('‚úÖ Attribute selector works');
  });

  test('TC1.4 - Test filling input and checking element visibility', async ({ page }) => {
    // Arrange: Get input element
    const inputField = page.locator('input[placeholder="What needs to be done?"]');

    // Assert: Input is empty initially
    await expect(inputField).toHaveValue('');
    console.log('‚úÖ Input is empty');

    // Act: Fill the input
    await inputField.fill('Learn Playwright');

    // Assert: Input has value
    await expect(inputField).toHaveValue('Learn Playwright');
    console.log('‚úÖ Input filled successfully');

    // Act: Clear it
    await inputField.clear();

    // Assert: Input is empty again
    await expect(inputField).toHaveValue('');
    console.log('‚úÖ Input cleared successfully');
  });

  test('TC1.5 - Test element visibility and DOM attachment checks', async ({ page }) => {
    // Arrange: Get multiple elements
    const heading = page.locator('h1');
    const input = page.locator('input[placeholder="What needs to be done?"]');
    const footer = page.locator('footer');

    // Assert: All elements are visible
    await expect(heading).toBeVisible();
    console.log('‚úÖ Heading is visible');

    await expect(input).toBeVisible();
    console.log('‚úÖ Input is visible');

    await expect(footer).toBeVisible();
    console.log('‚úÖ Footer is visible');

    // Assert: All elements are attached to DOM
    await expect(heading).toBeAttached();
    await expect(input).toBeAttached();
    await expect(footer).toBeAttached();
    console.log('‚úÖ All elements attached to DOM');

    // Assert: Elements are not hidden
    await expect(heading).not.toBeHidden();
    await expect(input).not.toBeHidden();
    await expect(footer).not.toBeHidden();
    console.log('‚úÖ All elements are not hidden');
  });

});

