const { test, expect } = require('@playwright/test');

test.describe('DAY 2: Locators, Actions & Waits', () => {

  test.beforeEach(async ({ page }) => {
    console.log('üîµ Starting Day 2 tests...');
    await page.goto('https://demo.playwright.dev/todomvc/');
  });

  // ===================================================
  // TASK 1: MASTER LOCATORS - 5 Different Types
  // ===================================================

  test('TC2.1 - Locator Type 1: CSS Selector', async ({ page }) => {
    console.log('\nüìç Test 1: CSS Selector');

    // CSS Selector: Find input with specific attribute
    const input = page.locator('input[placeholder="What needs to be done?"]');

    // Verify it's attached to DOM
    await expect(input).toBeAttached();
    console.log('‚úÖ CSS selector found element');

    // Check it's visible
    await expect(input).toBeVisible();
    console.log('‚úÖ Element is visible');
  });

  test('TC2.2 - Locator Type 2: Text Selector', async ({ page }) => {
    console.log('\nüìç Test 2: Text Selector');

    // Text Selector: Find by exact text
    const heading = page.locator('text=todos');

    // Verify found
    await expect(heading).toBeVisible();
    console.log('‚úÖ Text selector found element');
    console.log('‚úÖ Found element with text: "todos"');
  });

  test('TC2.3 - Locator Type 3: Role Selector (BEST PRACTICE)', async ({ page }) => {
    console.log('\nüìç Test 3: Role Selector');

    // Role Selector: Find by accessibility role (RECOMMENDED)
    const textbox = page.locator('role=textbox');

    // Verify found
    await expect(textbox).toBeVisible();
    console.log('‚úÖ Role selector found textbox');
    console.log('‚úÖ Role-based selectors are most reliable!');
  });

  test('TC2.4 - Locator Type 4: Attribute Selector', async ({ page }) => {
    console.log('\nüìç Test 4: Attribute Selector');

    // Attribute Selector: Match part of attribute
    // *= means "contains"
    const input = page.locator('[placeholder*="needs"]');

    // Verify found
    await expect(input).toBeVisible();
    console.log('‚úÖ Attribute selector found element');
    console.log('‚úÖ Used [placeholder*="needs"] pattern');
  });

  test('TC2.5 - Locator Type 5: XPath Selector', async ({ page }) => {
    console.log('\nüìç Test 5: XPath Selector');

    // XPath: Find by path in DOM tree
    const input = page.locator('xpath=//input[@placeholder="What needs to be done?"]');

    // Verify found
    await expect(input).toBeVisible();
    console.log('‚úÖ XPath selector found element');
    console.log('‚ö†Ô∏è  XPath works but CSS is often better');
  });

  // ===================================================
  // TASK 2: MASTER CLICK ACTION
  // ===================================================

  test('TC2.6 - Action: Click Button', async ({ page }) => {
    console.log('\nüñ±Ô∏è Test 6: Click Action');

    // Arrange: Get input and add button (implicit through Enter key)
    const input = page.locator('input[placeholder="What needs to be done?"]');

    // Act: Fill input
    await input.fill('Test item 1');
    console.log('‚úÖ Filled input with text');

    // Act: Press Enter to add item
    await input.press('Enter');
    console.log('‚úÖ Pressed Enter key');

    // Assert: Item added (new item appears)
    const newItem = page.locator('text=Test item 1');
    await expect(newItem).toBeVisible();
    console.log('‚úÖ Item added successfully');
  });

  // ===================================================
  // TASK 3: MASTER FILL ACTION
  // ===================================================

  test('TC2.7 - Action: Fill & Type', async ({ page }) => {
    console.log('\n‚å®Ô∏è Test 7: Fill & Type Actions');

    // Arrange
    const input = page.locator('input[placeholder="What needs to be done?"]');

    // Test 1: Fill (fast, sets value directly)
    await input.fill('Item with fill()');
    console.log('‚úÖ Used fill() - sets value directly');

    // Assert
    await expect(input).toHaveValue('Item with fill()');
    console.log('‚úÖ Value verified: "Item with fill()"');

    // Clear for next test
    await input.clear();
    console.log('‚úÖ Cleared input');

    // Test 2: Type (slow, simulates typing)
    await input.type('Item with type()');
    console.log('‚úÖ Used type() - simulates keystrokes');

    // Assert
    await expect(input).toHaveValue('Item with type()');
    console.log('‚úÖ Value verified: "Item with type()"');
  });

  // ===================================================
  // TASK 4: MASTER CHECK ACTION
  // ===================================================

  test('TC2.8 - Action: Check Checkbox', async ({ page }) => {
    console.log('\n‚òëÔ∏è Test 8: Check/Uncheck Actions');

    // Arrange: Add item first
    const input = page.locator('input[placeholder="What needs to be done?"]');
    await input.fill('Complete this task');
    await input.press('Enter');

    // Wait for item to appear
    await page.waitForTimeout(500);

    // Get checkbox
    const checkbox = page.locator('input[type="checkbox"]').first();

    // Act: Check the checkbox
    await checkbox.check();
    console.log('‚úÖ Checked checkbox');

    // Assert: Checkbox is checked
    await expect(checkbox).toBeChecked();
    console.log('‚úÖ Checkbox is now checked');

    // Act: Uncheck the checkbox
    await checkbox.uncheck();
    console.log('‚úÖ Unchecked checkbox');

    // Assert: Checkbox is unchecked
    const isChecked = await checkbox.isChecked();
    if (!isChecked) {
      console.log('‚úÖ Checkbox is now unchecked');
    }
  });

  // ===================================================
  // TASK 5: MASTER HOVER ACTION
  // ===================================================

  test('TC2.9 - Action: Hover Over Element', async ({ page }) => {
