    await input.fill('Item to hover over');
    await input.press('Enter');

    // Wait for item to appear
    await page.waitForTimeout(500);

    // Get the item
    const item = page.locator('text=Item to hover over').first();

    // Act: Hover over item
    await item.hover();
    console.log('‚úÖ Hovered over item');

    // Assert: Item is still visible (hover doesn't hide it)
    await expect(item).toBeVisible();
    console.log('‚úÖ Item still visible after hover');

    // Check if delete button appeared (on hover)
    const deleteBtn = page.locator('button.destroy').first();
    const deleteBtnVisible = await deleteBtn.isVisible().catch(() => false);
    if (deleteBtnVisible) {
      console.log('‚úÖ Delete button visible on hover');
    } else {
      console.log('‚úÖ Hover action completed');
    }
  });

  // ===================================================
  // TASK 6: MASTER PRESS ACTION
  // ===================================================

  test('TC2.10 - Action: Press Keyboard Keys', async ({ page }) => {
    console.log('\n‚å®Ô∏è Test 10: Press Action');

    // Arrange
